import React, { Component } from 'react';
import {Tabs, Tab, Grid, Cell,Card, CardTitle, CardAction, Button, CardMenu, IconButton } from 'react-mdl';

class Projects extends React.Component {
    constructor(props){
        super(props);
        this.state = { activeTab: 0}; 
    }

toggleCategories(){

    if(this.state.activeTab === 0){
        return(
            <Card shadow={5} style={{minWidth: '450', margin:'auto'}}>
                <CardTitle style={{color: '#ffffff', height: '176px', background: 'url(https://jelvix.com/images/blog/5a58d2b2bbd4b.jpg) center / cover'}}></CardTitle>
            </Card>
        )
    }else if(this.state.activeTab === 1){
        return(
            <div><h1>This is 2</h1></div>
        )
    }
    else if(this.state.activeTab === 2 ){
        return(
            <div><h1>This is 3</h1></div>
        )
    }
    else if(this.state.activeTab === 3){
        return(
            <div><h1>This is 4</h1></div>
        )
    }
}
    render(){
        return (
            <div className="category-tabs">
                <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({activeTab: tabId})} ripple >
                    <Tab>React</Tab>
                    <Tab>React</Tab>
                    <Tab>React</Tab>
                    <Tab>React</Tab>
                </Tabs>
                <section className="projects-grid">
                    <Grid className="projects-grid">
                        <Cell col={12}>
                            <div className="content">
                            {this.toggleCategories()} 
                            </div>
                        </Cell>
                    </Grid>
                </section>
            </div>

        )
    }
}

export default Projects;
